import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';

// import { css } from 'emotion/macro';
// const loaderMessageStyle = css`
//   .loading {
//     font-size: 3em;
//     font-weight: bold;
//     width: 100%;
//     text-align: center;
//     margin: 0;
//     padding-bottom: 3em;
//   }
// `;

const LoaderMessage = ({loadingMessage, isLoading}) => {
  const [showLoadingMessage, setShowLoadingMessage] = useState(false);

  useEffect(() => {
    let loadingMessageDelay;
    if(isLoading) {
      loadingMessageDelay = setTimeout( () => {}, 400);
      setShowLoadingMessage(true);
    }
    return () => {
      clearTimeout(loadingMessageDelay);
      setShowLoadingMessage(false);
    }
  }, [isLoading])
  return (
    <div aria-live="assertive">
      {showLoadingMessage && <p>{loadingMessage}</p>}
    </div>
  );
}

LoaderMessage.propTypes = {
  loadingMessage: PropTypes.string.isRequired,
  isLoading: PropTypes.bool,
};

export default LoaderMessage;

