import React, { useState, useEffect } from 'react';
import { getMenu } from '../data/iceCreamData';
import Helmet from 'react-helmet';

import { css } from 'emotion/macro';
import IceCreamImage from './IceCreamImage';
const cardContentStyle = css`
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  padding: 0.3em 0 0 0;

  p {
    margin: 0;
    color: #403147;
  }

  p.price {
    font-size: 1em;
    position: relative;
    margin-right: 1.125em;
    color: rgba(64, 49, 71, 0.8);

    &:after {
      content: '';
      width: 4px;
      height: 4px;
      position: absolute;
      top: 50%;
      margin-top: -3px;
      right: -0.7em;
      background: rgba(64, 49, 71, 0.4);
      border-radius: 50%;
    }
  }

  p.stock {
    font-size: 1em;
    color: rgba(64, 49, 71, 0.8);

    &.out {
      color: #d8474f;
    }
  }

  p.description {
    width: 100%;
    margin-top: 1em;
    line-height: 1.375em;
    color: rgba(64, 49, 71, 0.9);
    font-size: 0.875em;
  }
`;

const Menu = () => {
  const [menu, setMenu] = useState([]);

  useEffect(() => {
    let isMounted = true;
    getMenu().then(menuData => {
      if (isMounted) {
        setMenu(menuData);
      }
    });
    return () => {
      isMounted = false;
    }
  }, [])

  return (
    <main>
      <Helmet><title>Rock your taste buds with one of these | My Ice Cream</title></Helmet>
      <h2>Rock your taste buds with one of these</h2>
      {menu.length > 0 ?
        <ul>
          {menu.map( ({id, iceCream, price, description, inStock, quantity}) =>
          <li key={id.toString()}>
            <section>
              <div>
                <IceCreamImage iceCreamId={iceCream.id}/>
              </div>
              <div className={cardContentStyle}>
                <p className="price">{`$${price.toFixed(2)}`}</p>
                <p className={`stock${inStock ? '' : ' out'}`}>
                  {inStock ? `${quantity} in stock` : 'Currently out of stock!'}
                </p>
                <p className="description">{description}</p>
              </div>
            </section>
          </li>)}
        </ul>
      : 'nothing to show !' }
    </main>
  );
}

export default Menu; 
